it('should ignore fn.arity > 4', function (done) {
  var invoked = [];
  app.use(function (req, res, next, _a, _b) {
    invoked.push(0);
    next();
  });
  app.use(function (req, res, next) {
    invoked.push(1);
    next(new Error('err'));
  });
  app.use(function (err, req, res, next) {
    invoked.push(2);
    res.end(invoked.join(','));
  });
  request(app).get('/').expect(200, '1,2', done);
});

