it('should reuse the result if arguments are the same', async () => {
  // mocking CF for testing
  const expectedResult = {
    called: true
  };
  const promiseStub = sinon.stub().returns(Promise.resolve({
    called: true
  }));
  class FakeCF {
    describeStacks() {
      return {
        promise: promiseStub
      };
    }
  }
  const awsRequest = proxyquire('../../../../lib/aws/request', {
    './sdk-v2': {
      CloudFormation: FakeCF
    }
  });
  const numTests = 100;
  const executeRequest = () => awsRequest.memoized({
    name: 'CloudFormation',
    params: {
      credentials: {},
      useCache: true
    }
  }, 'describeStacks', {});
  const requests = [];
  for (let n = 0; n < numTests; n++) {
    requests.push(executeRequest());
  }
  return Promise.all(requests).then(results => {
    expect(Object.keys(results).length).to.equal(numTests);
    results.forEach(result => {
      expect(result).to.deep.equal(expectedResult);
    });
    expect(promiseStub).to.have.been.calledOnce;
  });
});

