it('should reject meaningfully invalid sources configuration', () => {
  expect(() => resolveExternalPluginSources({}, {
    sources: {},
    fulfilledSources: new Set()
  }, new Set([{
    configurationVariablesSources: 'foo'
  }]))).to.throw(ServerlessError).with.property('code', 'INVALID_VARIABLE_SOURCES_CONFIGURATION');
  expect(() => resolveExternalPluginSources({}, {
    sources: {
      existing: {
        resolve: () => {}
      }
    },
    fulfilledSources: new Set(['existing'])
  }, new Set([{
    configurationVariablesSources: {
      existing: {
        resolve: () => {}
      }
    }
  }]))).to.throw(ServerlessError).with.property('code', 'DUPLICATE_VARIABLE_SOURCE_CONFIGURATION');
  expect(() => resolveExternalPluginSources({}, {
    sources: {},
    fulfilledSources: new Set()
  }, new Set([{
    configurationVariablesSources: {
      source: 'foo'
    }
  }]))).to.throw(ServerlessError).with.property('code', 'INVALID_VARIABLE_SOURCE_CONFIGURATION');
  expect(() => resolveExternalPluginSources({}, {
    sources: {},
    fulfilledSources: new Set()
  }, new Set([{
    configurationVariablesSources: {
      source: {
        resolve: 'foo '
      }
    }
  }]))).to.throw(ServerlessError).with.property('code', 'INVALID_VARIABLE_SOURCE_RESOLVER_CONFIGURATION');
});

