  yesterday = yesterday.setDate(yesterday.getDate() - 1);
  yesterday = new Date(yesterday);
  const yesterdaysYear = yesterday.getFullYear();
  const yesterdaysMonth = yesterday.getMonth() + 1;
  const yesterdaysDay = yesterday.getDate();
