    }],
    // Added purely to test https://github.com/serverless/serverless/issues/7844 regression
    usagePlan: {
      quota: {
        limit: 5000
      }
    }
  };
  awsCompileApigEvents.compileApiKeys();
  const expectedApiKeys = [{
