    value: undefined,
    description: undefined,
    customerId: undefined
  }, {
    name: undefined,
    value: 'valueForKeyWithoutName',
    description: 'Api key description',
    customerId: undefined
  }, {
    name: '3456789012',
    value: 'valueForKey3456789012',
    description: undefined,
    customerId: undefined
  }, {
    name: '9876543211',
    value: 'valueForKey9876543211',
    description: undefined,
    customerId: 'customerid98765'
  }];
  expectedApiKeys.forEach((apiKey, index) => {
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Type).to.equal('AWS::ApiGateway::ApiKey');
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.Enabled).to.equal(true);
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.Name).to.equal(apiKey.name);
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.Description).to.equal(apiKey.description);
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.CustomerId).to.equal(apiKey.customerId);
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.Value).to.equal(apiKey.value);
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.StageKeys[0].RestApiId.Ref).to.equal('ApiGatewayRestApi');
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].Properties.StageKeys[0].StageName).to.equal('dev');
    expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources[awsCompileApigEvents.provider.naming.getApiKeyLogicalId(index + 1)].DependsOn).to.equal('ApiGatewayDeploymentTest');
  });
});

