it('should have request parameters defined when they are set', () => {
  awsCompileApigEvents.validated.events = [{
    functionName: 'First',
    http: {
      path: 'users/create',
      method: 'post',
      integration: 'AWS',
      request: {
        parameters: {
          'method.request.querystring.foo': true,
          'method.request.querystring.bar': false,
          'method.request.header.foo': true,
          'method.request.header.bar': false,
          'method.request.path.foo': true,
          'method.request.path.bar': false
        }
      },
      response: {
        statusCodes: {
          200: {
            pattern: ''
          }
        }
      }
    }
  }];
  awsCompileApigEvents.compileMethods();
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.header.foo']).to.equal(true);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.header.bar']).to.equal(false);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.querystring.foo']).to.equal(true);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.querystring.bar']).to.equal(false);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.path.foo']).to.equal(true);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayMethodUsersCreatePost.Properties.RequestParameters['method.request.path.bar']).to.equal(false);
});

