it('should create child resources only if there are predefined parent resources', () => {
  awsCompileApigEvents.serverless.service.provider.apiGateway = {
    restApiId: '6fyzt1pfpk',
    restApiRootResourceId: 'z5d4qh4oqi',
    restApiResources: {
      '/foo': 'axcybf2i39',
      '/users': 'zxcvbnmasd',
      '/users/friends': 'fcasdoojp1',
      '/groups': 'iuoyiusduo'
    }
  };
  awsCompileApigEvents.validated.events = [{
    http: {
      path: 'foo/bar',
      method: 'GET'
    }
  }, {
    http: {
      path: 'foo/bar',
      method: 'POST'
    }
  }, {
    http: {
      path: 'foo/bar',
      method: 'DELETE'
    }
  }, {
    http: {
      path: 'bar/-',
      method: 'GET'
    }
  }, {
    http: {
      path: 'bar/foo',
      method: 'GET'
    }
  }, {
    http: {
      path: 'bar/{id}/foobar',
      method: 'GET'
    }
  }, {
    http: {
      path: 'bar/{id}',
      method: 'GET'
    }
  }, {
    http: {
      path: 'users/friends/comments',
      method: 'GET'
    }
  }, {
    http: {
      path: 'users/me/posts',
      method: 'GET'
    }
  }, {
    http: {
      path: 'groups/categories',
      method: 'GET'
    }
  }];
  awsCompileApigEvents.compileResources();
  try {
    awsCompileApigEvents.getResourceId('users/{userId}');
    throw new Error('Expected API Gateway resource not found error, got success');
  } catch (e) {
    expect(e.message).to.equal('Can not find API Gateway resource from path users/{userId}');
  }
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceFoo).to.equal(undefined);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceBar.Properties.RestApiId).to.equal('6fyzt1pfpk');
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceBar.Properties.ParentId).to.equal('z5d4qh4oqi');
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceFooBar.Properties.ParentId).to.equal('axcybf2i39');
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceBarIdVar.Properties.ParentId.Ref).to.equal('ApiGatewayResourceBar');
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceUsersMePosts).not.equal(undefined);
  expect(awsCompileApigEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.ApiGatewayResourceUsersFriendsComments.Properties.ParentId).to.equal('fcasdoojp1');
});

