it('should merge all preflight cors options for a path', () => {
  awsCompileApigEvents.serverless.service.functions = {
    first: {
      events: [{
        http: {
          method: 'GET',
          path: 'users',
          cors: {
            origins: ['http://example.com'],
            allowCredentials: true,
            maxAge: 10000,
            cacheControl: 'max-age=600, s-maxage=600, proxy-revalidate'
          }
        }
      }, {
        http: {
          method: 'POST',
          path: 'users',
          cors: {
            origins: ['http://example2.com'],
            maxAge: 86400
          }
        }
      }, {
        http: {
          method: 'PUT',
          path: 'users/{id}',
          cors: {
            headers: ['TestHeader']
          }
        }
      }, {
        http: {
          method: 'DELETE',
          path: 'users/{id}',
          cors: {
            headers: ['TestHeader2']
          }
        }
      }]
    }
  };
  const validated = awsCompileApigEvents.validate();
  expect(validated.corsPreflight['users/{id}'].methods).to.deep.equal(['OPTIONS', 'DELETE', 'PUT']);
  expect(validated.corsPreflight.users.origins).to.deep.equal(['http://example2.com', 'http://example.com']);
  expect(validated.corsPreflight['users/{id}'].headers).to.deep.equal(['TestHeader2', 'TestHeader']);
  expect(validated.corsPreflight.users.maxAge).to.equal(86400);
  expect(validated.corsPreflight.users.cacheControl).to.equal('max-age=600, s-maxage=600, proxy-revalidate');
  expect(validated.corsPreflight.users.allowCredentials).to.equal(true);
  expect(validated.corsPreflight['users/{id}'].allowCredentials).to.equal(false);
});

