it('should create a longest-common suffix of logGroup to minimize scope', () => {
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1'])).to.equal('/aws/lambda/hello1');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1', '/aws/lambda/hello2'])).to.equal('/aws/lambda/hello*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1', '/aws/lambda/hot'])).to.equal('/aws/lambda/h*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1', '/aws/lambda/tweet'])).to.equal('/aws/lambda/*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1', '/aws/lex/log1', '/aws/lightsail/log1'])).to.equal('/aws/l*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/hello1', '/aws/batch/log1'])).to.equal('/aws/*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/*', '/aws/lambda/hello'])).to.equal('/aws/*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/*', '/aws/lambda/hello'])).to.equal('/aws/lambda/*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda', '/aws/lambda/hello'])).to.equal('/aws/lambda*');
  expect(awsCompileCloudWatchLogEvents.longestCommonSuffix(['/aws/lambda/yada-dev-dummy', '/aws/lambda/yada-dev-dummy2'])).to.equal('/aws/lambda/yada-dev-dummy*');
});

