  expect(awsCompileIoTEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstIotTopicRule2.Properties.TopicRulePayload.RuleDisabled).to.equal(false);
  expect(awsCompileIoTEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstLambdaPermissionIotTopicRule1.Type).to.equal('AWS::Lambda::Permission');
