it('should create event source mappings when a DynamoDB stream ARN is given', () => {
  awsCompileStreamEvents.serverless.service.functions = {
    first: {
      events: [{
        stream: {
          arn: 'arn:aws:dynamodb:region:account:table/foo/stream/1',
          batchSize: 1,
          startingPosition: 'STARTING_POSITION_ONE',
          enabled: false
        }
      }, {
        stream: {
          arn: 'arn:aws:dynamodb:region:account:table/bar/stream/2',
          batchWindow: 15,
          maximumRetryAttempts: 4
        }
      }, {
        stream: 'arn:aws:dynamodb:region:account:table/baz/stream/3'
      }, {
        stream: {
          arn: 'arn:aws:dynamodb:region:account:table/buzz/stream/4',
          bisectBatchOnFunctionError: true,
          batchWindow: 0,
          maximumRecordAgeInSeconds: 120
        }
      }, {
        stream: {
          arn: 'arn:aws:dynamodb:region:account:table/fizz/stream/5',
          destinations: {
            onFailure: 'arn:aws:sns:region:account:snstopic'
          }
        }
      }]
    }
  };
  awsCompileStreamEvents.compileStreamEvents();

  // event 1
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.Type).to.equal('AWS::Lambda::EventSourceMapping');
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.DependsOn).to.include('IamRoleLambdaExecution');
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.Properties.EventSourceArn).to.equal(awsCompileStreamEvents.serverless.service.functions.first.events[0].stream.arn);
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.Properties.BatchSize).to.equal(awsCompileStreamEvents.serverless.service.functions.first.events[0].stream.batchSize);
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.Properties.StartingPosition).to.equal(awsCompileStreamEvents.serverless.service.functions.first.events[0].stream.startingPosition);
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbFoo.Properties.Enabled).to.equal(false);

  // event 2
