it('should allow specifying DynamoDB and Kinesis streams as CFN reference types', () => {
  awsCompileStreamEvents.serverless.service.resources.Parameters = {
    SomeDdbTableStreamArn: {
      Type: 'String'
    },
    ForeignKinesisStreamArn: {
      Type: 'String'
    }
  };
  awsCompileStreamEvents.serverless.service.functions = {
    first: {
      events: [{
        stream: {
          arn: {
            'Fn::GetAtt': ['SomeDdbTable', 'StreamArn']
          },
          type: 'dynamodb'
        }
      }, {
        stream: {
          arn: {
            'Fn::ImportValue': 'ForeignKinesis'
          },
          type: 'kinesis'
        }
      }, {
        stream: {
          arn: {
            'Fn::Join': [':', ['arn', 'aws', 'kinesis', {
              Ref: 'AWS::Region'
            }, {
              Ref: 'AWS::AccountId'
            }, 'stream/MyStream']]
          },
          type: 'kinesis'
        }
      }, {
        stream: {
          arn: {
            Ref: 'SomeDdbTableStreamArn'
          },
          type: 'dynamodb'
        }
      }, {
        stream: {
          arn: {
            Ref: 'ForeignKinesisStreamArn'
          },
          type: 'kinesis'
        }
      }]
    }
  };
  awsCompileStreamEvents.compileStreamEvents();

  // dynamodb with Fn::GetAtt
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingDynamodbSomeDdbTable.Properties.EventSourceArn).to.deep.equal({
    'Fn::GetAtt': ['SomeDdbTable', 'StreamArn']
  });
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.IamRoleLambdaExecution.Properties.Policies[0].PolicyDocument.Statement[0]).to.deep.equal({
    Action: ['dynamodb:GetRecords', 'dynamodb:GetShardIterator', 'dynamodb:DescribeStream', 'dynamodb:ListStreams'],
    Effect: 'Allow',
    Resource: [{
      'Fn::GetAtt': ['SomeDdbTable', 'StreamArn']
    }, {
      Ref: 'SomeDdbTableStreamArn'
    }]
  });

  // kinesis with Fn::ImportValue
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingKinesisForeignKinesis.Properties.EventSourceArn).to.deep.equal({
    'Fn::ImportValue': 'ForeignKinesis'
  });

  // kinesis with Fn::Join
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.FirstEventSourceMappingKinesisMyStream.Properties.EventSourceArn).to.deep.equal({
    'Fn::Join': [':', ['arn', 'aws', 'kinesis', {
      Ref: 'AWS::Region'
    }, {
      Ref: 'AWS::AccountId'
    }, 'stream/MyStream']]
  });
  expect(awsCompileStreamEvents.serverless.service.provider.compiledCloudFormationTemplate.Resources.IamRoleLambdaExecution.Properties.Policies[0].PolicyDocument.Statement[1]).to.deep.equal({
    Effect: 'Allow',
    Action: ['kinesis:GetRecords', 'kinesis:GetShardIterator', 'kinesis:DescribeStream', 'kinesis:ListStreams'],
    Resource: [{
      'Fn::ImportValue': 'ForeignKinesis'
    }, {
      'Fn::Join': [':', ['arn', 'aws', 'kinesis', {
        Ref: 'AWS::Region'
      }, {
        Ref: 'AWS::AccountId'
      }, 'stream/MyStream']]
    }, {
      Ref: 'ForeignKinesisStreamArn'
    }]
  });
});

