      }
    }
  });
  const {
    cfTemplate: originalTemplate,
    awsNaming
  } = await runServerless({
    cwd: serviceDir,
    command: 'package'
  });
  const functionCfLogicalId = awsNaming.getLambdaLogicalId('basic');
  const originalVersionCfConfig = Object.values(originalTemplate.Resources).find(resource => resource.Type === 'AWS::Lambda::Version' && resource.Properties.FunctionName.Ref === functionCfLogicalId).Properties;
  await updateConfig({
    disabledDeprecations: ['LAMBDA_HASHING_VERSION_PROPERTY'],
    provider: {
      lambdaHashingVersion: '20200924'
    }
  });
  const {
    cfTemplate: updatedTemplate
  } = await runServerless({
    cwd: serviceDir,
    command: 'package'
  });
  const updatedVersionCfConfig = Object.values(updatedTemplate.Resources).find(resource => resource.Type === 'AWS::Lambda::Version' && resource.Properties.FunctionName.Ref === functionCfLogicalId).Properties;
  expect(originalVersionCfConfig.CodeSha256).to.deep.equal(updatedVersionCfConfig.CodeSha256);
});

