    Action: 's3:*',
    Effect: 'Deny',
    Principal: '*',
    Resource: [{
      'Fn::Join': ['', ['arn:', {
        Ref: 'AWS::Partition'
      }, ':s3:::', {
        Ref: 'ServerlessDeploymentBucket'
      }, '/*']]
    }, {
      'Fn::Join': ['', ['arn:', {
        Ref: 'AWS::Partition'
      }, ':s3:::', {
        Ref: 'ServerlessDeploymentBucket'
      }]]
    }],
    Condition: {
      Bool: {
        'aws:SecureTransport': false
      }
    }
  });
}));

