it('should zip a whole service (without include / exclude usage)', async () => {
  params.zipFileName = getTestArtifactFileName('whole-service');
  return expect(packagePlugin.zip(params)).to.eventually.be.equal(path.join(serverless.serviceDir, '.serverless', params.zipFileName)).then(artifact => {
    const data = fs.readFileSync(artifact);
    return expect(zip.loadAsync(data)).to.be.fulfilled;
  }).then(unzippedData => {
    const unzippedFileData = unzippedData.files;
    expect(Object.keys(unzippedFileData).filter(file => !unzippedFileData[file].dir)).to.be.lengthOf(12);

    // root directory
    expect(unzippedFileData['event.json'].name).to.equal('event.json');
    expect(unzippedFileData['handler.js'].name).to.equal('handler.js');
    expect(unzippedFileData['file-1'].name).to.equal('file-1');
    expect(unzippedFileData['file-2'].name).to.equal('file-2');

    // bin directory
    expect(unzippedFileData['bin/binary-777'].name).to.equal('bin/binary-777');
    expect(unzippedFileData['bin/binary-444'].name).to.equal('bin/binary-444');

    // lib directory
    expect(unzippedFileData['lib/file-1.js'].name).to.equal('lib/file-1.js');
    expect(unzippedFileData['lib/directory-1/file-1.js'].name).to.equal('lib/directory-1/file-1.js');

    // node_modules directory
    expect(unzippedFileData['node_modules/directory-1/file-1'].name).to.equal('node_modules/directory-1/file-1');
    expect(unzippedFileData['node_modules/directory-1/file-2'].name).to.equal('node_modules/directory-1/file-2');
    expect(unzippedFileData['node_modules/directory-2/file-1'].name).to.equal('node_modules/directory-2/file-1');
    expect(unzippedFileData['node_modules/directory-2/file-2'].name).to.equal('node_modules/directory-2/file-2');
  });
});

it('should keep file permissions', async () => {
  params.zipFileName = getTestArtifactFileName('file-permissions');
  return expect(packagePlugin.zip(params)).to.eventually.be.equal(path.join(serverless.serviceDir, '.serverless', params.zipFileName)).then(artifact => {
    const data = fs.readFileSync(artifact);
    return expect(zip.loadAsync(data)).to.be.fulfilled;
  }).then(unzippedData => {
    const unzippedFileData = unzippedData.files;
    if (os.platform() !== 'win32') {
      // chmod does not work right on windows
      // binary file is set with chmod of 777
      expect(unzippedFileData['bin/binary-777'].unixPermissions).to.equal(Math.pow(2, 15) + 0o755);

      // read only file is set with chmod of 444
      expect(unzippedFileData['bin/binary-444'].unixPermissions).to.equal(Math.pow(2, 15) + 0o644);
    }
  });
});

