it('handles nested, complex objects', () => {
  const input = {
    b: 'shouldBeLast',
    a: {
      d: 'nestedPlainValue',
      c: {
        f: 'shouldBeLast',
        e: 'shouldBeFirst'
      }
    }
  };
  const result = deepSortObjectByKey(input);
  const expectedResult = JSON.stringify({
    a: {
      c: {
        e: 'shouldBeFirst',
        f: 'shouldBeLast'
      },
      d: 'nestedPlainValue'
    },
    b: 'shouldBeLast'
  });
  expect(JSON.stringify(result)).to.equal(expectedResult);
});

