it("assert.calledWith exception message with large object arguments", function () {
  const calledArg = [{
    first: "a",
    second: {
      nest: true
    },
    third: [{
      fourth: {
        nest: true
      }
    }],
    mismatchKey: true
  }, "fifth"];
  this.obj.doSomething(calledArg);
  const expectedArg = [{
    first: "a",
    second: {
      nest: true
    },
    third: [{
      fourth: {
        nest: false
      }
    }],
    mismatchKeyX: true
  }, "fifth"];
  const actual = this.message("calledWith", this.obj.doSomething, expectedArg);

  /**
   * Unfortunately, `util.inspect` behaves differently in node than
   * it does in browsers, so we need to detect the difference in order
   * to set the correct value for expected `expected`.
   *
   * In node the output uses more whitespace than in browsers.
   *
   * @type {boolean}
   */
  const usesCondensedFormat = inspect([{
    apple: "e4d13f88-9b9b-4e05-8abb-f76df2d4ef40"
  }, {
    pear: "841b661f-80f4-4560-9cf4-133dcffd240c"
  }]).indexOf("[ {") === 0;
  const expected = usesCondensedFormat ? `${"expected doSomething to be called with arguments \n" + "[ { first: 'a',\n" + "    second: { nest: true },\n" + "    third: [ [Object] ],\n"}${color.red("    mismatchKey: true },\n")}${color.green("    mismatchKeyX: true },\n")}  'fifth' ]` : `${"expected doSomething to be called with arguments \n" + "[\n" + "  {\n" + "    first: 'a',\n" + "    second: { nest: true },\n" + "    third: [ [Object] ],\n"}${color.red("    mismatchKey: true\n")}${color.green("    mismatchKeyX: true\n")}  },\n` + `  'fifth'\n` + `]`;
  assert.equals(actual, expected);
});

