it("oneLine", function () {
  function verify(arg, expected) {
    const faux = createFaux();
    faux(arg);
    assert.equals(faux.printf("%C").replace(/ at.*/g, ""), `\n    ${expected}`);
  }
  verify(true, "faux(true)");
  verify(false, "faux(false)");
  verify(undefined, "faux(undefined)");
  verify(1, "faux(1)");
  verify(0, "faux(0)");
  verify(-1, "faux(-1)");
