      next(new Error("Authentication error"));
    });
    socket.use((event, next) => {
      done(new Error("should not happen"));
    });
    socket.on("join", () => {
      done(new Error("should not happen"));
    });
    socket.on("error", (err) => {
      expect(err).to.be.an(Error);
      expect(err.message).to.eql("Authentication error");

      success(done, io, clientSocket);
    });
  });
});

