  hook.open = function () {
    i++;
    hook.callback("" + "function attachEvent(event, listener) {" + "    if (typeof window.addEventListener !== 'undefined') {" + "        window.addEventListener(event, listener, false);" + "    } else {" + "        document.attachEvent('on' + event, listener);" + "        window.attachEvent('on' + event, listener);" + "    }" + "}" + "attachEvent('message', function(e) {" + "    var b = e.data;" + "    parent.postMessage(window_id + ' ' + 'e', '*');" + "});" + "parent.postMessage(window_id + ' ' + 's', '*');");
