test('Fails on invalid relations', async () => {
  const res = await rq({
    method: 'POST',
    url: '/content-type-builder/content-types',
    body: {
      contentType: {
        kind: 'singleType',
        singularName: 'test-st',
        pluralName: 'test-sts',
        displayName: 'Tests-st',
        attributes: {
          relation: {
            type: 'relation',
            relation: 'oneTo',
            target: 'plugin::users-permissions.user',
            targetAttribute: 'test'
          }
        }
      }
    }
  });
  expect(res.statusCode).toBe(400);
  expect(res.body).toMatchObject({
    error: {
      details: {
        errors: [{
          message: 'must be one of the following values: oneToOne, oneToMany, morphOne, morphMany, morphToOne, morphToMany',
          name: 'ValidationError',
          path: ['contentType', 'attributes', 'relation', 'relation']
        }]
      },
      message: 'must be one of the following values: oneToOne, oneToMany, morphOne, morphMany, morphToOne, morphToMany',
      name: 'ValidationError'
    }
  });
});

test('Cannot switch collectionType to singleType when multiple entries in DB', async () => {
  const createRes = await rq({
    method: 'POST',
    url: '/content-type-builder/content-types',
    body: {
      contentType: {
        kind: 'collectionType',
        displayName: 'test-collection',
        singularName: 'test-collection',
        pluralName: 'test-collections',
        attributes: {
          title: {
            type: 'string'
          }
        }
      }
    }
  });
  expect(createRes.statusCode).toBe(201);
  await restart();
  const {
    uid
  } = createRes.body.data; // create data

  for (let i = 0; i < 2; i++) {
    await strapi.query(uid).create({
      data: {
        title: 'Test'
      }
    });
  }

  const updateRes = await rq({
    method: 'PUT',
    url: `/content-type-builder/content-types/${uid}`,
    body: {
      contentType: {
        kind: 'singleType',
        displayName: 'test-collection',
        singularName: 'test-collection',
        pluralName: 'test-collections',
        attributes: {
          title: {
            type: 'string'
          }
        }
      }
    }
  });
  expect(updateRes.statusCode).toBe(400);
  expect(updateRes.body.error).toMatch('multiple entries in DB');
});

