test('Can move folders and files into another folder', async () => {
  const folder1 = await createFolder('folder-b-1', null);
  const folder1a = await createFolder('folder-b-1a', folder1.id);
  const folder1b = await createFolder('folder-b-1b', folder1.id);
  const folder1a1 = await createFolder('folder-b-1a1', folder1a.id);
  const file1 = await createAFile(null);
  const file1a = await createAFile(folder1a.id);
  const file1b = await createAFile(folder1b.id);
  const file1a1 = await createAFile(folder1a1.id);
  const res = await rq({
    method: 'POST',
    url: '/upload/actions/bulk-move',
    body: {
      destinationFolderId: folder1b.id,
      fileIds: [file1a.id],
      folderIds: [folder1a.id]
    }
  });
  expect(res.body.data).toMatchObject({
    files: [{
      alternativeText: null,
      caption: null,
      createdAt: expect.anything(),
      ext: '.jpg',
      folderPath: folder1b.path,
      formats: null,
      hash: expect.anything(),
      height: 20,
      id: file1a.id,
      mime: 'image/jpeg',
      name: 'rec.jpg',
      previewUrl: null,
      provider: 'local',
      provider_metadata: null,
      size: 0.27,
      updatedAt: expect.anything(),
      url: expect.anything(),
      width: 20
    }],
    folders: [{
      id: folder1a.id,
      name: 'folder-b-1a',
      path: `${folder1b.path}/${folder1a.pathId}`,
      pathId: expect.any(Number),
      createdAt: expect.anything(),
      updatedAt: expect.anything()
    }]
  });
  const {
    body: {
      data: folderResults
    }
  } = await rq({
    method: 'GET',
    url: '/upload/folders',
    qs: {
      populate: 'parent',
      sort: 'id:asc',
      filters: {
        id: {
          $in: [folder1.id, folder1a.id, folder1b.id, folder1a1.id]
        }
      }
    }
  });
  expect(folderResults[0]).toMatchObject({ ...folder1,
    parent: null
  });
  expect(folderResults[1]).toMatchObject({ ...folder1a,
    path: `${folder1b.path}/${folder1a.pathId}`,
    parent: {
      id: folder1b.id
    },
    updatedAt: expect.anything()
  });
  expect(folderResults[2]).toMatchObject({ ...folder1b,
    parent: {
      id: folder1.id
    }
  });
  expect(folderResults[3]).toMatchObject({ ...folder1a1,
    path: `${folder1b.path}/${folder1a.pathId}/${folder1a1.pathId}`,
    parent: {
      id: folder1a.id
    }
  });
  const {
    body: {
      results: fileResults
    }
  } = await rq({
    method: 'GET',
    url: '/upload/files',
    qs: {
      pageSize: 100,
      populate: 'folder',
      sort: 'id:asc',
      filters: {
        id: {
          $in: [file1.id, file1a.id, file1b.id, file1a1.id]
        }
      }
    }
  });
  expect(fileResults[0]).toMatchObject({ ...file1,
    folder: null
  });
  expect(fileResults[1]).toMatchObject({ ...file1a,
    folderPath: folder1b.path,
    folder: {
      id: folder1b.id
    },
    updatedAt: expect.anything()
  });
  expect(fileResults[2]).toMatchObject({ ...file1b,
    folder: {
      id: folder1b.id
    }
  });
  expect(fileResults[3]).toMatchObject({ ...file1a1,
    folderPath: `${folder1b.path}/${folder1a.pathId}/${folder1a1.pathId}`,
    folder: {
      id: folder1a1.id
    }
  });
  data.folders.push(...folderResults);
  data.files.push(...fileResults);
});

