test('Delete a locale and entities in this locale', async () => {
  const {
    body: frenchProduct
  } = await rq({
    url: '/content-manager/collection-types/api::product.product',
    method: 'POST',
    qs: {
      plugins: {
        i18n: {
          locale: 'fr-FR'
        }
      }
    },
    body: {
      name: 'product name'
    }
  });
  await rq({
    url: '/content-manager/collection-types/api::product.product',
    method: 'POST',
    qs: {
      plugins: {
        i18n: {
          locale: 'en',
          relatedEntityId: frenchProduct.id
        }
      }
    },
    body: {
      name: 'product name'
    }
  });
  const {
    body: {
      results: createdProducts
    }
  } = await rq({
    url: '/content-manager/collection-types/api::product.product',
    method: 'GET',
    qs: {
      locale: 'fr-FR'
    }
  });
  expect(createdProducts).toHaveLength(1);
  expect(createdProducts[0].localizations[0].locale).toBe('en');
  const res = await rq({
    url: `/i18n/locales/${data.locales[1].id}`,
    method: 'DELETE'
  });
  const {
    body: {
      results: frenchProducts
    }
  } = await rq({
    url: '/content-manager/collection-types/api::product.product',
    method: 'GET',
    qs: {
      locale: 'fr-FR'
    }
  });
  expect(frenchProducts).toHaveLength(0);
  const {
    body: {
      results: englishProducts
    }
  } = await rq({
    url: '/content-manager/collection-types/api::product.product',
    method: 'GET',
    qs: {
      locale: 'en'
    }
  });
  expect(englishProducts).toHaveLength(1);
  expect(res.statusCode).toBe(200);
  expect(res.body).toMatchObject(omitTimestamps(data.locales[1]));
  data.deletedLocales.push(res.body);
  data.locales.splice(1, 1);
});

