it('shows the table on button click', () => {
  cy.get('@propsBtn').click();
  cy.get('@container').find('table').should('contain', 'Prop name');
});

it('renders component preview', () => {
  cy.get('@preview').find('button', {
    timeout: 10000
  }).contains('Push Me').should('exist');
});

it('changes the render after code change', () => {
  const codeToSkip = '</Button>';
  cy.get('@container').find('textarea').type(`${'{leftarrow}'.repeat(codeToSkip.length)} Harder`);
  cy.get('@preview').find('button').contains('Push Me Harder').should('exist');
});

it('toggles isolated example mode correctly', () => {
  cy.get('[data-testid$="-examples"]').as('componentExamples'); // Toggle into isolated example mode

  cy.get('@componentExamples').find('[data-testid$="-isolate-button"]').first().click(); // Assert that there is only one example showing

  cy.get('@componentExamples').find('[data-testid*="-example-"]').should('have.length', 1); // Toggle out of isolated example mode

  cy.get('[data-testid$="-isolate-button"]').click(); // Assert the other examples are showing again

  cy.get('@componentExamples').find('[data-testid*="-example-"]').should('have.length.above', 1); // Check that we've returned to isolated component mode instead of normal mode
  // TODO: this is currently bugged (returns to normal mode rather than isolated component mode)
  //cy.get('[id$=container]').should('have.length', 1);
});

it('shows multiple components in normal mode', () => {
  cy.get('[data-testid$=-container]').should('have.length.above', 1);
});

it('toggles isolated component mode correctly', () => {
  cy.get('[data-testid=sidebar]').as('sidebar'); // Toggle into isolated mode

  cy.get('[data-testid$="-isolate-button"]').first().click(); // Assert there's only one component showing

  cy.get('[data-testid$=-container]').should('have.length', 1); // Assert the sidebar is no longer showing

  cy.get('@sidebar').should('not.exist'); // Toogle out of isolated mode

  cy.get('[data-testid$="-isolate-button"]').first().click(); // Assert that more than one component is now showing

  cy.get('[data-testid$=-container]').should('have.length.above', 1); // Asser that the sidebar is now showing again

  cy.get('@sidebar').should('exist');
});
