  expect(morpher.type()).toBe(ObjectBag);
  expect(morpher.at(0.5)).toEqual(any(Object));
  expect(morpher.at(0.5).valueOf()).toEqual(objectContaining({
    a: new SVGNumber(7.5),
    b: new SVGNumber(15)
  }));
});

