it('adds an item to the end of the queue', function () {
  var queue = new Queue();
  queue.push(1);
  const item = queue.push(2);
  queue.push(3);
  queue.remove(item);
  queue.push(item);
  expect(queue.first()).toBe(1);
  expect(queue.last()).toBe(2);
});

it('removes the given item from the queue', function () {
  var queue = new Queue();
  queue.push(1);
  queue.push(2);
  var item = queue.push(3);
  queue.remove(item);
  expect(queue.last()).toBe(2);
  expect(queue.first()).toBe(1);
});

it('removes the given item from the queue', function () {
  var queue = new Queue();
  var item = queue.push(1);
  queue.push(2);
  queue.push(3);
  queue.remove(item);
  expect(queue.last()).toBe(3);
  expect(queue.first()).toBe(2);
});

it('returns nothing if queue is empty', function () {
  var queue = new Queue();
  var val = queue.shift();
  expect(val).toBeFalsy();
});

it('returns the first item of the queue and removes it', function () {
  var queue = new Queue();
  queue.push(1);
  queue.push(2);
  queue.push(3);
  var val = queue.shift();
  expect(queue.last()).toBe(3);
  expect(queue.first()).toBe(2);
  expect(val).toBe(1);
});

it('can handle all form of input', () => {
  var fn = Runner.sanitise;
  expect(fn(200, 200, 'now')).toEqual(objectContaining({
    duration: 200,
    delay: 200,
    when: 'now',
    times: 1,
    wait: 0,
    swing: false
  }));
  expect(fn(200, 200)).toEqual(objectContaining({
    duration: 200,
    delay: 200,
    when: 'last',
    times: 1,
    wait: 0,
    swing: false
  }));
  expect(fn(200)).toEqual(objectContaining({
    duration: 200,
    delay: defaults.timeline.delay,
    when: 'last',
    times: 1,
    wait: 0,
    swing: false
  }));
  expect(fn(runFn)).toEqual(objectContaining({
    duration: runFn,
    delay: defaults.timeline.delay,
    when: 'last',
    times: 1,
    wait: 0,
    swing: false
  }));
  expect(fn({
    delay: 200
  })).toEqual(objectContaining({
    duration: defaults.timeline.duration,
    delay: 200,
    when: 'last',
    times: 1,
    wait: 0,
    swing: false
  }));
  expect(fn({
    times: 3,
    delay: 200,
    when: 'now',
    swing: true,
    wait: 200
  })).toEqual(objectContaining({
    duration: defaults.timeline.duration,
    delay: 200,
    when: 'now',
    times: 3,
    wait: 200,
    swing: true
  }));
});

