it('sets the progress of a runner when looping', () => {
  var spy = createSpy('stepper');
  var runner = new Runner(800).queue(null, spy).loop(10, false, 200); // progress 0.5 somewhere in the middle of wait time

  expect(runner.progress(0.5).progress()).toBe(0.5);
  expect(spy).toHaveBeenCalledWith(1); // start of next loop

  runner.step(100);
  expect(spy).toHaveBeenCalledWith(0); // should move 0.5 into the next loop

  expect(runner.progress(5400 / 9800).progress()).toBe(5400 / 9800);
  expect(spy.calls.mostRecent().args[0]).toBeCloseTo(0.5);
});

