it('can use a tailwind.config.js configuration file with ESM syntax', async () => {
  await removeFile('tailwind.config.js');
  await writeInputFile('index.html', html`
      <link rel="stylesheet" href="./index.css" />
      <div class="z-primary"></div>
    `);
  await writeInputFile('index.css', css`
      @tailwind base;
      @tailwind components;
      @tailwind utilities;
    `);
  await writeInputFile('../tailwind.config.js', javascript`
      export default {
        content: ['./src/index.html'],
        theme: {
          extend: {
            zIndex: {
              primary: 0,
            },
          },
        },
        corePlugins: {
          preflight: false,
        },
      }
    `);
  await $('parcel build ./src/index.html --no-cache', {
    env: {
      NODE_ENV: 'production'
    }
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .z-primary {
        z-index: 0;
      }
    `);
});

