test('classes are generated when the html file changes', async () => {
  await writeInputFile('index.html', html`
      <link rel="stylesheet" href="./index.css" />
      <div class="font-bold"></div>
    `);
  let runningProcess = $('parcel watch ./src/index.html --no-cache');
  await waitForOutputFileCreation(/index\.\w+\.css$/);
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .font-bold {
        font-weight: 700;
      }
    `);
  await waitForOutputFileChange(/index\.\w+\.css$/, async () => {
    await appendToInputFile('index.html', html`<div class="font-normal"></div>`);
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .font-bold {
        font-weight: 700;
      }
      .font-normal {
        font-weight: 400;
      }
    `);
  await waitForOutputFileChange(/index\.\w+\.css$/, async () => {
    await appendToInputFile('index.html', html`<div class="z-0"></div>`);
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .z-0 {
        z-index: 0;
      }
      .font-bold {
        font-weight: 700;
      }
      .font-normal {
        font-weight: 400;
      }
    `);
  return runningProcess.stop();
});

test('classes are generated when the tailwind.config.js file changes', async () => {
  await writeInputFile('index.html', html`
      <link rel="stylesheet" href="./index.css" />
      <div class="font-bold md:font-medium"></div>
    `);
  let runningProcess = $('parcel watch ./src/index.html --no-cache');
  await waitForOutputFileCreation(/index\.\w+\.css$/);
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .font-bold {
        font-weight: 700;
      }
      @media (width >= 768px) {
        .md\:font-medium {
          font-weight: 500;
        }
      }
    `);
  await waitForOutputFileChange(/index\.\w+\.css$/, async () => {
    await writeInputFile('../tailwind.config.js', javascript`
        module.exports = {
          content: ['./src/index.html'],
          theme: {
            extend: {
              screens: {
                md: '800px'
              },
              fontWeight: {
                bold: 'bold'
              }
            },
          },
          corePlugins: {
            preflight: false,
          },
          plugins: [],
        }
    `);
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .font-bold {
        font-weight: bold;
      }
      @media (width >= 800px) {
        .md\:font-medium {
          font-weight: 500;
        }
      }
    `);
  return runningProcess.stop();
});

test('classes are generated when the index.css file changes', async () => {
  await writeInputFile('index.html', html`
      <link rel="stylesheet" href="./index.css" />
      <div class="btn font-bold"></div>
    `);
  let runningProcess = $('parcel watch ./src/index.html --no-cache');
  await waitForOutputFileCreation(/index\.\w+\.css$/);
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .font-bold {
        font-weight: 700;
      }
    `);
  await waitForOutputFileChange(/index\.\w+\.css$/, async () => {
    await writeInputFile('index.css', css`
        @tailwind base;
        @tailwind components;
        @tailwind utilities;

        @layer components {
          .btn {
            @apply rounded px-2 py-1;
          }
        }
      `);
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .btn {
        border-radius: 0.25rem;
        padding: 0.25rem 0.5rem;
      }

      .font-bold {
        font-weight: 700;
      }
    `);
  await waitForOutputFileChange(/index\.\w+\.css$/, async () => {
    await writeInputFile('index.css', css`
        @tailwind base;
        @tailwind components;
        @tailwind utilities;

        @layer components {
          .btn {
            @apply rounded z-0 px-2 py-1;
          }
        }
      `);
  });
  expect(await readOutputFile(/index\.\w+\.css$/)).toIncludeCss(css`
      .btn {
        z-index: 0;
        border-radius: 0.25rem;
        padding: 0.25rem 0.5rem;
      }
      .font-bold {
        font-weight: 700;
      }
    `);
  return runningProcess.stop();
});

