      expect(result.css).toMatchFormattedCss(css``);
    } else {
      expect(result.css).toMatchFormattedCss(css`
        .group\/\$\{id\}:hover .group-hover\/\$\{id\}\:visible {
          visibility: visible;
        }
      `);
    }
  });
});

