          color: rgb(0 0 0 / var(--tw-text-opacity));
          font-weight: 700;
        }
      }
      @supports (foo: bar) {
        @media (min-width: 768px) {
          .some-apply-thing {
            --tw-text-opacity: 1;
            color: rgb(0 0 0 / var(--tw-text-opacity));
            font-weight: 700;
          }
        }
      }
      @media (min-width: 640px) {
        .sm\:text-center {
          text-align: center;
        }
        .sm\:font-bold {
          font-weight: 700;
        }
      }
      @media (min-width: 768px) {
        .md\:text-center {
          text-align: center;
        }
        .md\:font-bold {
          font-weight: 700;
        }
      }
      @media (min-width: 1024px) {
        .lg\:text-center {
          text-align: center;
        }
        .lg\:font-bold {
          font-weight: 700;
        }
      }
    `);
  });
});

