test('plugins should cache correctly', () => {
  let plugin = createPlugin.withOptions(({
    className = 'banana'
  } = {}) => ({
    addComponents
  }) => {
    addComponents({
      [`.${className}`]: {
        position: 'absolute'
      }
    });
  });
  let config = {
    content: [{
      raw: html`<div class="banana sm:banana apple sm:apple"></div>`
    }],
    corePlugins: [],
    theme: {
      screens: {
        sm: '400px'
      }
    }
  };
  function internalRun(options = {}) {
    return run('@tailwind components', {
      ...config,
      plugins: [plugin(options)]
    });
  }
  return Promise.all([internalRun(), internalRun({
    className: 'apple'
  })]).then(([result1, result2]) => {
    let expected1 = css`
        .banana {
          position: absolute;
        }
        @media (min-width: 400px) {
          .sm\:banana {
            position: absolute;
          }
        }
      `;
    let expected2 = css`
        .apple {
          position: absolute;
        }
        @media (min-width: 400px) {
          .sm\:apple {
            position: absolute;
          }
        }
      `;
    expect(result1.css).toMatchFormattedCss(expected1);
    expect(result2.css).toMatchFormattedCss(expected2);
  });
});

