it('should warn when we detect nested css', () => {
  let config = {
    content: [{
      raw: html`<div class="nested"></div>`
    }]
  };
  let input = css`
    @tailwind utilities;

    .nested {
      .example {
      }
    }
  `;
  return run(input, config).then(result => {
    expect(result.messages).toHaveLength(1);
    expect(result.messages).toMatchObject([{
      type: 'warning',
      text: ['Nested CSS was detected, but CSS nesting has not been configured correctly.', 'Please enable a CSS nesting plugin *before* Tailwind in your configuration.', 'See how here: https://tailwindcss.com/docs/using-with-preprocessors#nesting'].join('\n')
    }]);
  });
});

it('should not warn when we detect nested css inside css @layer rules', () => {
  let config = {
    content: [{
      raw: html`<div class="underline"></div>`
    }]
  };
  let input = css`
    @layer tw-base, tw-components, tw-utilities;
    @layer tw-utilities {
      @tailwind utilities;
    }
  `;
  return run(input, config).then(result => {
    expect(result.css).toMatchFormattedCss(css`
      @layer tw-base, tw-components, tw-utilities;
      @layer tw-utilities {
        .underline {
          text-decoration-line: underline;
        }
      }
    `);
    expect(result.messages).toHaveLength(0);
  });
});

