test('custom user-land utilities', () => {
  let config = {
    content: [{
      raw: html`<div
          class="focus:hover:align-chocolate align-banana hover:align-banana uppercase"
        ></div>`
    }],
    corePlugins: {
      preflight: false
    },
    theme: {},
    plugins: []
  };
  let input = css`
    @layer utilities {
      .align-banana {
        text-align: banana;
      }
    }

    @tailwind base;
    @tailwind components;
    @tailwind utilities;

    @layer utilities {
      .align-chocolate {
        text-align: chocolate;
      }
    }
  `;
  return run(input, config).then(result => {
    expect(result.css).toMatchFormattedCss(css`
      ${defaults}
      .uppercase {
        text-transform: uppercase;
      }
      .align-banana,
      .hover\:align-banana:hover {
        text-align: banana;
      }
      .focus\:hover\:align-chocolate:hover:focus {
        text-align: chocolate;
      }
    `);
  });
});

test('comments can be used inside layers without crashing', () => {
  let config = {
    content: [{
      raw: html`<div class="important-utility important-component"></div>`
    }],
    corePlugins: {
      preflight: false
    },
    theme: {},
    plugins: []
  };
  let input = css`
    @tailwind base;
    @tailwind components;
    @tailwind utilities;

    @layer base {
      /* Important base */
      div {
        background-color: #bada55;
      }
    }

    @layer utilities {
      /* Important utility */
      .important-utility {
        text-align: banana;
      }
    }

    @layer components {
      /* Important component */
      .important-component {
        text-align: banana;
      }
    }
  `;
  return run(input, config).then(result => {
    expect(result.css).toMatchFormattedCss(css`
      div {
        background-color: #bada55;
      }
      ${defaults}
      .important-component,
        .important-utility {
        text-align: banana;
      }
    `);
  });
});

