test('Theme function in JS can apply alpha values to colors (1)', () => {
  let input = css`
    @tailwind utilities;
  `;
  let output = css`
    .text-foo {
      color: #3b82f680;
    }
  `;
  return run(input, {
    content: [{
      raw: html`text-foo`
    }],
    corePlugins: {
      textOpacity: false
    },
    theme: {
      colors: {
        blue: {
          500: '#3b82f6'
        }
      },
      extend: {
        textColor: ({
          theme
        }) => ({
          foo: theme('colors.blue.500 / 50%')
        })
      }
    }
  }).then(result => {
    expect(result.css).toMatchFormattedCss(output);
    expect(result.warnings().length).toBe(0);
  });
});

