it('should be possible to parse multiple applications at once', () => {
  const input = ['spin 1s linear infinite', 'ping 1s cubic-bezier(0, 0, 0.2, 1) infinite', 'pulse 2s cubic-bezier(0.4, 0, 0.6) infinite'].join(',');
  const parsed = parseAnimationValue(input);
  expect(parsed).toHaveLength(3);
  expect(parsed).toEqual([{
    value: 'spin 1s linear infinite',
    name: 'spin',
    duration: '1s',
    timingFunction: 'linear',
    iterationCount: 'infinite'
  }, {
    value: 'ping 1s cubic-bezier(0, 0, 0.2, 1) infinite',
    name: 'ping',
    duration: '1s',
    timingFunction: 'cubic-bezier(0, 0, 0.2, 1)',
    iterationCount: 'infinite'
  }, {
    value: 'pulse 2s cubic-bezier(0.4, 0, 0.6) infinite',
    name: 'pulse',
    duration: '2s',
    timingFunction: 'cubic-bezier(0.4, 0, 0.6)',
    iterationCount: 'infinite'
  }]);
});

