test('it parses simple single class definitions', () => {
  let result = parseObjectStyles({
    '.foobar': {
      backgroundColor: 'red',
      color: 'white',
      padding: '1rem'
    }
  });
  expect(toCss(result)).toMatchFormattedCss(css`
    .foobar {
      background-color: red;
      color: white;
      padding: 1rem;
    }
  `);
});

test('it parses multiple class definitions', () => {
  let result = parseObjectStyles({
