it('callback execution should be cancellable', function () {
  var cancelSpy = jasmine.createSpy().andCallFake(cancel);
  this.target.onSync('one', cancelSpy).onSync('one', this.spy).onAsync('two', cancelSpy).onAsync('two', this.spy).onSync('three', cancelSpy).onAsync('three', this.spy).trigger('one two three');
  waitsFor(assertCallCount(cancelSpy, 3));
  waitsFor(assertCallCount(this.spy, 0));

  function cancel() {
    return false;
