it('should export the same things as the server-side version', () => {
  for (let key in validator) {
    if ({}.hasOwnProperty.call(validator, key)) {
      assert.strictEqual(typeof validator[key], typeof min[key], `Minified version did not export ${key}`);
    }
  }
});

it('should be up to date', () => {
  assert.strictEqual(min.version, validator.version, 'Minified version mismatch. Run `make min`');
});

it('should validate strings', () => {
  assert.strictEqual(min.isEmail('foo@bar.com'), true);
  assert.strictEqual(min.isEmail('foo'), false);
});

it('should sanitize strings', () => {
  assert.strictEqual(min.toBoolean('1'), true);
});

it('should export validators', () => {
  assert.strictEqual(typeof validator.isEmail, 'function');
  assert.strictEqual(typeof validator.isAlpha, 'function');
});

it('should export sanitizers', () => {
  assert.strictEqual(typeof validator.toBoolean, 'function');
  assert.strictEqual(typeof validator.toFloat, 'function');
});

it('should export the version number', () => {
  /* eslint-disable global-require */
  assert.strictEqual(validator.version, require('../package.json').version, 'Version number mismatch in "package.json" vs. "validator.js"');
  /* eslint-enable global-require */
});

it('should export isPostalCode\'s supported locales', () => {
  assert.ok(isPostalCodeLocales instanceof Array);
  assert.ok(validator.isPostalCodeLocales instanceof Array);
});

it('should export isAlpha\'s supported locales', () => {
  assert.ok(isAlphaLocales instanceof Array);
  assert.ok(validator.isAlphaLocales instanceof Array);
});

it('should export isAlphanumeric\'s supported locales', () => {
  assert.ok(isAlphanumericLocales instanceof Array);
  assert.ok(validator.isAlphanumericLocales instanceof Array);
});

it('should export isMobilePhone\'s supported locales', () => {
  assert.ok(isMobilePhoneLocales instanceof Array);
  assert.ok(validator.isMobilePhoneLocales instanceof Array);
});

it('should export isFloat\'s supported locales', () => {
  assert.ok(isFloatLocales instanceof Array);
  assert.ok(validator.isFloatLocales instanceof Array);
});

it('should export a list of country codes that implement IBAN', () => {
  assert.ok(ibanCountryCodes instanceof Array);
  assert.ok(validator.ibanLocales instanceof Array);
});

it('should sanitize boolean strings', () => {
  test({
    sanitizer: 'toBoolean',
    expect: {
      0: false,
      '': false,
      1: true,
      true: true,
      True: true,
      TRUE: true,
      foobar: true,
      '   ': true,
      false: false,
      False: false,
      FALSE: false
    }
  });
  test({
    sanitizer: 'toBoolean',
    args: [true],
    // strict
    expect: {
      0: false,
      '': false,
      1: true,
      true: true,
      True: true,
      TRUE: true,
      foobar: false,
      '   ': false,
      false: false,
      False: false,
      FALSE: false
    }
  });
});

it('should trim whitespace', () => {
  test({
    sanitizer: 'trim',
    expect: {
      '  \r\n\tfoo  \r\n\t   ': 'foo',
      '      \r': ''
    }
  });
  test({
    sanitizer: 'ltrim',
    expect: {
      '  \r\n\tfoo  \r\n\t   ': 'foo  \r\n\t   ',
      '   \t  \n': ''
    }
  });
  test({
    sanitizer: 'rtrim',
    expect: {
      '  \r\n\tfoo  \r\n\t   ': '  \r\n\tfoo',
      ' \r\n  \t': ''
    }
  });
});

it('should trim custom characters', () => {
  test({
    sanitizer: 'trim',
    args: ['01'],
    expect: {
      '010100201000': '2'
    }
  });
  test({
    sanitizer: 'ltrim',
    args: ['01'],
    expect: {
      '010100201000': '201000'
    }
  });
  test({
    sanitizer: 'ltrim',
    args: ['\\S'],
    expect: {
      '\\S01010020100001': '01010020100001'
    }
  });
  test({
    sanitizer: 'rtrim',
    args: ['01'],
    expect: {
      '010100201000': '0101002'
    }
  });
  test({
    sanitizer: 'rtrim',
    args: ['\\S'],
    expect: {
      '01010020100001\\S': '01010020100001'
    }
  });
});

it('should convert strings to integers', () => {
  test({
    sanitizer: 'toInt',
    expect: {
      3: 3,
      ' 3 ': 3,
      2.4: 2,
      foo: NaN
    }
  });
  test({
    sanitizer: 'toInt',
    args: [16],
    expect: {
      ff: 255
    }
  });
});

it('should convert strings to floats', () => {
  test({
    sanitizer: 'toFloat',
    expect: {
      2: 2.0,
      '2.': 2.0,
      '-2.5': -2.5,
      '.5': 0.5,
      '2020-01-06T14:31:00.135Z': NaN,
      foo: NaN
    }
  });
});

it('should escape HTML', () => {
  test({
    sanitizer: 'escape',
    expect: {
      '<script> alert("xss&fun"); </script>': '&lt;script&gt; alert(&quot;xss&amp;fun&quot;); &lt;&#x2F;script&gt;',
      "<script> alert('xss&fun'); </script>": '&lt;script&gt; alert(&#x27;xss&amp;fun&#x27;); &lt;&#x2F;script&gt;',
      'Backtick: `': 'Backtick: &#96;',
      'Backslash: \\': 'Backslash: &#x5C;'
    }
  });
});

it('should unescape HTML', () => {
  test({
    sanitizer: 'unescape',
    expect: {
      '&lt;script&gt; alert(&quot;xss&amp;fun&quot;); &lt;&#x2F;script&gt;': '<script> alert("xss&fun"); </script>',
      '&lt;script&gt; alert(&#x27;xss&amp;fun&#x27;); &lt;&#x2F;script&gt;': "<script> alert('xss&fun'); </script>",
      'Backtick: &#96;': 'Backtick: `',
      'Escaped string: &amp;lt;': 'Escaped string: &lt;'
    }
  });
});

it('should remove control characters (<32 and 127)', () => {
  // Check basic functionality
  test({
    sanitizer: 'stripLow',
    expect: {
      'foo\x00': 'foo',
      '\x7Ffoo\x02': 'foo',
      '\x01\x09': '',
      'foo\x0A\x0D': 'foo'
    }
  });
  // Unicode safety
  test({
    sanitizer: 'stripLow',
    expect: {
      perché: 'perch\u00e9',
      '\u20ac': '\u20ac',
      '\u2206\x0A': '\u2206',
      '\ud83d\ude04': '\ud83d\ude04'
    }
  });
  // Preserve newlines
  test({
    sanitizer: 'stripLow',
    args: [true],
    // keep_new_lines
    expect: {
      'foo\x0A\x0D': 'foo\x0A\x0D',
      '\x03foo\x0A\x0D': 'foo\x0A\x0D'
    }
  });
});

it('should sanitize a string based on a whitelist', () => {
  test({
    sanitizer: 'whitelist',
    args: ['abc'],
    expect: {
      abcdef: 'abc',
      aaaaaaaaaabbbbbbbbbb: 'aaaaaaaaaabbbbbbbbbb',
      a1b2c3: 'abc',
      '   ': ''
    }
  });
});

it('should sanitize a string based on a blacklist', () => {
  test({
    sanitizer: 'blacklist',
    args: ['abc'],
    expect: {
      abcdef: 'def',
      aaaaaaaaaabbbbbbbbbb: '',
      a1b2c3: '123',
      '   ': '   '
    }
  });
});

it('should score passwords', () => {
  test({
    sanitizer: 'isStrongPassword',
    args: [{
      returnScore: true,
      pointsPerUnique: 1,
      pointsPerRepeat: 0.5,
      pointsForContainingLower: 10,
      pointsForContainingUpper: 10,
      pointsForContainingNumber: 10,
      pointsForContainingSymbol: 10
    }],
    expect: {
      abc: 13,
      abcc: 13.5,
      aBc: 23,
      'Abc123!': 47,
      '!@#$%^&*()': 20
    }
  });
});

it('should score passwords with default options', () => {
  test({
    sanitizer: 'isStrongPassword',
    expect: {
      abc: false,
      abcc: false,
      aBc: false,
      'Abc123!': false,
      '!@#$%^&*()': false,
      'abc123!@f#rA': true
    }
  });
});

it('should normalize an email based on domain', () => {
  test({
    sanitizer: 'normalizeEmail',
    expect: {
      'test@me.com': 'test@me.com',
      'some.name@gmail.com': 'somename@gmail.com',
      'some.name@googleMail.com': 'somename@gmail.com',
      'some.name+extension@gmail.com': 'somename@gmail.com',
      'some.Name+extension@GoogleMail.com': 'somename@gmail.com',
      'some.name.middleName+extension@gmail.com': 'somenamemiddlename@gmail.com',
      'some.name.middleName+extension@GoogleMail.com': 'somenamemiddlename@gmail.com',
      'some.name.midd.leNa.me.+extension@gmail.com': 'somenamemiddlename@gmail.com',
      'some.name.midd.leNa.me.+extension@GoogleMail.com': 'somenamemiddlename@gmail.com',
      'some.name+extension@unknown.com': 'some.name+extension@unknown.com',
      'hans@m端ller.com': 'hans@m端ller.com',
      'some.name.midd..leNa...me...+extension@GoogleMail.com': 'somenamemidd..lena...me...@gmail.com',
      'matthew..example@gmail.com': 'matthew..example@gmail.com',
      '"foo@bar"@baz.com': '"foo@bar"@baz.com',
      'test@ya.ru': 'test@yandex.ru',
      'test@yandex.kz': 'test@yandex.ru',
      'test@yandex.ru': 'test@yandex.ru',
      'test@yandex.ua': 'test@yandex.ru',
      'test@yandex.com': 'test@yandex.ru',
      'test@yandex.by': 'test@yandex.ru',
      '@gmail.com': false,
      '@icloud.com': false,
      '@outlook.com': false,
      '@yahoo.com': false
    }
  });

  // Testing all_lowercase switch, should apply to domains not known to be case-insensitive
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      all_lowercase: false
    }],
    expect: {
      'test@foo.com': 'test@foo.com',
      'hans@m端ller.com': 'hans@m端ller.com',
      'test@FOO.COM': 'test@foo.com',
      // Hostname is always lowercased
      'blAH@x.com': 'blAH@x.com',
      // In case of domains that are known to be case-insensitive, there's a separate switch
      'TEST@me.com': 'test@me.com',
      'TEST@ME.COM': 'test@me.com',
      'SOME.name@GMAIL.com': 'somename@gmail.com',
      'SOME.name.middleName+extension@GoogleMail.com': 'somenamemiddlename@gmail.com',
      'SOME.name.midd.leNa.me.+extension@gmail.com': 'somenamemiddlename@gmail.com',
      'SOME.name@gmail.com': 'somename@gmail.com',
      'SOME.name@yahoo.ca': 'some.name@yahoo.ca',
      'SOME.name@outlook.ie': 'some.name@outlook.ie',
      'SOME.name@me.com': 'some.name@me.com',
      'SOME.name@yandex.ru': 'some.name@yandex.ru'
    }
  });

  // Testing *_lowercase
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      all_lowercase: false,
      gmail_lowercase: false,
      icloud_lowercase: false,
      outlookdotcom_lowercase: false,
      yahoo_lowercase: false,
      yandex_lowercase: false
    }],
    expect: {
      'TEST@FOO.COM': 'TEST@foo.com',
      // all_lowercase
      'ME@gMAil.com': 'ME@gmail.com',
      // gmail_lowercase
      'ME@me.COM': 'ME@me.com',
      // icloud_lowercase
      'ME@icloud.COM': 'ME@icloud.com',
      // icloud_lowercase
      'ME@outlook.COM': 'ME@outlook.com',
      // outlookdotcom_lowercase
      'JOHN@live.CA': 'JOHN@live.ca',
      // outlookdotcom_lowercase
      'ME@ymail.COM': 'ME@ymail.com',
      // yahoo_lowercase
      'ME@yandex.RU': 'ME@yandex.ru' // yandex_lowercase
    }
  });

  // Testing all_lowercase
  // Should overwrite all the *_lowercase options
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      all_lowercase: true,
      gmail_lowercase: false,
      // Overruled
      icloud_lowercase: false,
      // Overruled
      outlookdotcom_lowercase: false,
      // Overruled
      yahoo_lowercase: false // Overruled
    }],

    expect: {
      'TEST@FOO.COM': 'test@foo.com',
      // all_lowercase
      'ME@gMAil.com': 'me@gmail.com',
      // gmail_lowercase
      'ME@me.COM': 'me@me.com',
      // icloud_lowercase
      'ME@icloud.COM': 'me@icloud.com',
      // icloud_lowercase
      'ME@outlook.COM': 'me@outlook.com',
      // outlookdotcom_lowercase
      'JOHN@live.CA': 'john@live.ca',
      // outlookdotcom_lowercase
      'ME@ymail.COM': 'me@ymail.com' // yahoo_lowercase
    }
  });

  // Testing *_remove_dots
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_remove_dots: false
    }],
    expect: {
      'SOME.name@GMAIL.com': 'some.name@gmail.com',
      'SOME.name+me@GMAIL.com': 'some.name@gmail.com',
      'my.self@foo.com': 'my.self@foo.com'
    }
  });
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_remove_dots: true
    }],
    expect: {
      'SOME.name@GMAIL.com': 'somename@gmail.com',
      'SOME.name+me@GMAIL.com': 'somename@gmail.com',
      'some.name..multiple@gmail.com': 'somename..multiple@gmail.com',
      'my.self@foo.com': 'my.self@foo.com'
    }
  });

  // Testing *_remove_subaddress
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_remove_subaddress: false,
      icloud_remove_subaddress: false,
      outlookdotcom_remove_subaddress: false,
      yahoo_remove_subaddress: false // Note Yahoo uses "-"
    }],

    expect: {
      'foo+bar@unknown.com': 'foo+bar@unknown.com',
      'foo+bar@gmail.com': 'foo+bar@gmail.com',
      // gmail_remove_subaddress
      'foo+bar@me.com': 'foo+bar@me.com',
      // icloud_remove_subaddress
      'foo+bar@icloud.com': 'foo+bar@icloud.com',
      // icloud_remove_subaddress
      'foo+bar@live.fr': 'foo+bar@live.fr',
      // outlookdotcom_remove_subaddress
      'foo+bar@hotmail.co.uk': 'foo+bar@hotmail.co.uk',
      // outlookdotcom_remove_subaddress
      'foo-bar@yahoo.com': 'foo-bar@yahoo.com',
      // yahoo_remove_subaddress
      'foo+bar@yahoo.com': 'foo+bar@yahoo.com' // yahoo_remove_subaddress
    }
  });

  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_remove_subaddress: true,
      icloud_remove_subaddress: true,
      outlookdotcom_remove_subaddress: true,
      yahoo_remove_subaddress: true // Note Yahoo uses "-"
    }],

    expect: {
      'foo+bar@unknown.com': 'foo+bar@unknown.com',
      'foo+bar@gmail.com': 'foo@gmail.com',
      // gmail_remove_subaddress
      'foo+bar@me.com': 'foo@me.com',
      // icloud_remove_subaddress
      'foo+bar@icloud.com': 'foo@icloud.com',
      // icloud_remove_subaddress
      'foo+bar@live.fr': 'foo@live.fr',
      // outlookdotcom_remove_subaddress
      'foo+bar@hotmail.co.uk': 'foo@hotmail.co.uk',
      // outlookdotcom_remove_subaddress
      'foo-bar@yahoo.com': 'foo@yahoo.com',
      // yahoo_remove_subaddress
      'foo+bar@yahoo.com': 'foo+bar@yahoo.com' // yahoo_remove_subaddress
    }
  });

  // Testing gmail_convert_googlemaildotcom
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_convert_googlemaildotcom: false
    }],
    expect: {
      'SOME.name@GMAIL.com': 'somename@gmail.com',
      'SOME.name+me@GMAIL.com': 'somename@gmail.com',
      'SOME.name+me@googlemail.com': 'somename@googlemail.com',
      'SOME.name+me@googlemail.COM': 'somename@googlemail.com',
      'SOME.name+me@googlEmail.com': 'somename@googlemail.com',
      'my.self@foo.com': 'my.self@foo.com'
    }
  });
  test({
    sanitizer: 'normalizeEmail',
    args: [{
      gmail_convert_googlemaildotcom: true
    }],
    expect: {
      'SOME.name@GMAIL.com': 'somename@gmail.com',
      'SOME.name+me@GMAIL.com': 'somename@gmail.com',
      'SOME.name+me@googlemail.com': 'somename@gmail.com',
      'SOME.name+me@googlemail.COM': 'somename@gmail.com',
      'SOME.name+me@googlEmail.com': 'somename@gmail.com',
      'my.self@foo.com': 'my.self@foo.com'
    }
  });
});

it('should validate different typeOf', () => {
  assert.strictEqual(typeOf([]), 'array');
  assert.strictEqual(typeOf(null), 'null');
  assert.strictEqual(typeOf({}), 'object');
  assert.strictEqual(typeOf(new Date()), 'date');
  assert.strictEqual(typeOf('ezkemboi'), 'string');
  assert.strictEqual(typeOf(String('kemboi')), 'string');
  assert.strictEqual(typeOf(undefined), 'undefined');
  assert.strictEqual(typeOf(2021), 'number');
  assert.notStrictEqual(typeOf([]), 'object');
});

