    validator: null,
    define(module) {
      window.validator = module();
    }
  };
  window.define.amd = true;
  let sandbox = vm.createContext(window);
  vm.runInContext(validator_js, sandbox);
  assert.strictEqual(window.validator.trim('  foobar '), 'foobar');
});

