it('Error stack printed to stderr', async () => {
  try {
    await fs.writeFile(
      indexPage,
      `export default function Page() {
        return <div/>
      }
        export function getStaticProps() {
          // Make it pass on the build phase
          if(process.env.NEXT_PHASE === "${PHASE_PRODUCTION_BUILD}") {
            return { props: { foo: 'bar' }, revalidate: 1 }
          }

          throw new Error("Oops")
        }
        `
    )

    await nextBuild(appDir)

    appPort = await findPort()

    let stderr = ''
    app = await nextStart(appDir, appPort, {
      onStderr: (msg) => {
        stderr += msg || ''
      },
    })
    await check(async () => {
      await renderViaHTTP(appPort, '/')
      return stderr
    }, /error: oops/i)

    expect(stderr).toContain('Error: Oops')
    expect(stderr).toContain(`\n    at getStaticProps`)
  } finally {
    await killApp(app)
  }
})

it('Does not have duplicate script references', async () => {
  const html = await renderViaHTTP(appPort, '/')
  const $ = cheerio.load(html)
  let foundDuplicate = false
  const srcs = new Set()

  for (const script of $('script').toArray()) {
    const { src } = script.attribs
    if (!src || !src.startsWith('/_next/static')) continue
    if (srcs.has(src)) {
      console.error(`Found duplicate script ${src}`)
      foundDuplicate = true
    }
    srcs.add(src)
  }
  expect(foundDuplicate).toBe(false)
})

