    "// top-level comment
    {
      // in-object comment 1
      \\"compilerOptions\\": {
        // in-object comment
        \\"esModuleInterop\\": true, // this should be true
        \\"module\\": \\"esnext\\" // should not be umd
        // end-object comment
        ,
        \\"target\\": \\"es5\\",
        \\"lib\\": [
          \\"dom\\",
          \\"dom.iterable\\",
          \\"esnext\\"
        ],
        \\"allowJs\\": true,
        \\"skipLibCheck\\": true,
        \\"strict\\": false,
        \\"forceConsistentCasingInFileNames\\": true,
        \\"noEmit\\": true,
        \\"incremental\\": true,
        \\"moduleResolution\\": \\"node\\",
        \\"resolveJsonModule\\": true,
        \\"isolatedModules\\": true,
        \\"jsx\\": \\"preserve\\"
      }
      // in-object comment 2
      ,
      \\"include\\": [
        \\"next-env.d.ts\\",
        \\"**/*.ts\\",
        \\"**/*.tsx\\"
      ],
      \\"exclude\\": [
        \\"node_modules\\"
      ]
    }
    // end comment
    "
  `)
})

