it('should add when missing (single, non-array)', () => {
  const input = {
    module: {
      rules: [{ use: 'rr' }],
    },
  }

  attachReactRefresh(input, 'rr')
  expect(input).toMatchObject({
    module: {
      rules: [
        {
          use: [
            expect.stringMatching(
              /react-refresh-utils[\\/]dist[\\/]loader\.js/
            ),
            'rr',
          ],
        },
      ],
    },
  })
})

