it('Test `detectRoutes` with `featHandleMiss=true`, `cleanUrls=true`', async () => {
  const options = {
    featHandleMiss: true,
    cleanUrls: true,
  };

  const testHeaders = (redirectRoutes: Route[] | null) => {
    if (!redirectRoutes || redirectRoutes.length === 0) {
      throw new Error('Expected one redirect but found none');
    }
    expect(redirectRoutes).toBeDefined();
    expect(redirectRoutes.length).toBe(2);
  };

  {
    const files = ['api/user.go', 'api/team.js', 'api/package.json'];

    const {
      defaultRoutes,
      redirectRoutes,
      rewriteRoutes,
      errorRoutes,
      limitedRoutes,
    } = await detectBuilders(files, null, options);
    testHeaders(redirectRoutes);
    expect(defaultRoutes).toStrictEqual([]);
    expect(rewriteRoutes).toStrictEqual([
      {
        status: 404,
        src: '^/api(/.*)?$',
      },
    ]);
    expect(errorRoutes).toStrictEqual([
      {
        status: 404,
        src: '^(?!/api).*$',
        dest: '/404',
      },
    ]);

    // Limited routes should have js but not go since the go plugin is not installed
    expect(limitedRoutes).toStrictEqual({
      redirectRoutes: [
        {
          src: '^/(api(?:.+)?)/index(?:\\.(?:js))?/?$',
          headers: {
            Location: '/$1',
          },
          status: 308,
        },
        {
          src: '^/api/(.+)(?:\\.(?:js))/?$',
          headers: {
            Location: '/api/$1',
          },
          status: 308,
