it('should have correct extensions', async () => {
  const builders = [
    {
      use: '@vercel/node',
      src: 'api/**/*.js',
      config: {
        zeroConfig: true,
      },
    },
    {
      use: '@vercel/python',
      src: 'api/**/*.py',
      config: {
        zeroConfig: true,
      },
    },
    {
      use: '@vercel/go',
      src: 'api/**/*.go',
      config: {
        zeroConfig: true,
      },
    },
    {
      use: '@vercel/ruby',
      src: 'api/**/*.rb',
      config: {
        zeroConfig: true,
      },
    },
    {
      use: 'now-bash',
      src: 'api/**/*.sh',
      // No zero config so it should not be added
    },
    {
      use: 'now-no-extension',
      src: 'api/executable',
      // No extension should not be added
      config: {
        zeroConfig: true,
      },
    },
    {
      use: '@vercel/next',
      src: 'package.json',
      // No api directory should not be added
      config: {
        zeroConfig: true,
      },
    },
    {
      use: 'now-rust@1.0.1',
      src: 'api/user.rs',
      config: {
        zeroConfig: true,
        functions: {
          'api/**/*.rs': {
            runtime: 'now-rust@1.0.1',
          },
        },
      },
    },
  ];
  const result = detectApiExtensions(builders);
  expect(result.size).toBe(5);
  expect(result.has('.js')).toBe(true);
  expect(result.has('.py')).toBe(true);
  expect(result.has('.go')).toBe(true);
  expect(result.has('.rb')).toBe(true);
  expect(result.has('.rs')).toBe(true);
});

it('should error when builds in vercel.json', async () => {
  const vercelConfig = {
    builds: [{ use: '@vercel/node', src: 'api/**/*.js' }],
  };
  const files = {
    'vercel.json': JSON.stringify(vercelConfig),
    'api/foo.js': 'console.log("foo")',
  };
  const result = await detectFileSystemAPI({
    files,
    projectSettings: {},
    builders: vercelConfig.builds,
    vercelConfig,
    pkg: null,
    tag: '',
    enableFlag: true,
  });
  expect(result).toEqual({
    fsApiBuilder: null,
    reason:
      'Detected `builds` in vercel.json. Please remove it in favor of CLI plugins.',
    metadata: { hasDotOutput: false, hasMiddleware: false, plugins: [] },
  });
});

