test('should normalize all redirects before rewrites', () => {
  const nowConfig = {
    cleanUrls: true,
    rewrites: [{
      source: '/v1',
      destination: '/v2/api.py'
    }],
    redirects: [{
      source: '/help',
      destination: '/support',
      statusCode: 302
    }, {
      source: '/bug',
      destination: 'https://example.com/bug',
      statusCode: 308
    }]
  };
  const actual = getTransformedRoutes({
    nowConfig
  });
  const expected = [{
    src: '^/(?:(.+)/)?index(?:\\.html)?/?$',
    headers: {
      Location: '/$1'
    },
    status: 308
  }, {
    src: '^/(.*)\\.html/?$',
    headers: {
      Location: '/$1'
    },
    status: 308
  }, {
    src: '^/help$',
    headers: {
      Location: '/support'
    },
    status: 302
  }, {
    src: '^/bug$',
    headers: {
      Location: 'https://example.com/bug'
    },
    status: 308
  }, {
    handle: 'filesystem'
  }, {
    src: '^/v1$',
    dest: '/v2/api.py',
    check: true
  }];
  assert.deepEqual(actual.error, null);
  assert.deepEqual(actual.routes, expected);
  assertValid(actual.routes, routesSchema);
});

