test('mergeRoutes simple', () => {
  const userRoutes = [{
    src: '/user1',
    dest: '/u1'
  }, {
    src: '/user2',
    dest: '/u2'
  }];
  const builds = [{
    use: '@vercel/node',
    entrypoint: 'api/home.js',
    routes: [{
      src: '/node1',
      dest: '/n1'
    }, {
      src: '/node2',
      dest: '/n2'
    }]
  }, {
    use: '@vercel/python',
    entrypoint: 'api/users.py',
    routes: [{
      src: '/python1',
      dest: '/py1'
    }, {
      src: '/python2',
      dest: '/py2'
    }]
  }];
  const actual = mergeRoutes({
    userRoutes,
    builds
  });
  const expected = [{
    dest: '/u1',
    src: '/user1'
  }, {
    dest: '/u2',
    src: '/user2'
  }, {
    dest: '/n1',
    src: '/node1'
  }, {
    dest: '/n2',
    src: '/node2'
  }, {
    dest: '/py1',
    src: '/python1'
  }, {
    dest: '/py2',
    src: '/python2'
  }];
  deepStrictEqual(actual, expected);
});

