      'vercel-dev': '',
      'vercel-build': '',
      dev: '',
      build: '',
    },
  };
  assert.equal(
    getScriptName(pkg, ['vercel-dev', 'now-dev', 'dev']),
    'vercel-dev'
  );
  assert.equal(
    getScriptName(pkg, ['vercel-build', 'now-build', 'build']),
