it('should return `null`', () => {
  assert.equal(getScriptName(undefined, ['build']), null);
  assert.equal(getScriptName({}, ['build']), null);
  assert.equal(getScriptName({ scripts: {} }, ['build']), null);

  const pkg = {
    scripts: {
      dev: '',
      build: '',
